mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT a.acc_category, a.acc_name, a.acc_num, a.acc_address, r.roo_price as acc_rowprice FROM accommdation a JOIN room r ON a.acc_num=r.acc_num WHERE r.roo_price IN (SELECT min(roo_price) FROM room GROUP BY acc_num) AND a.acc_status = 1
and acc_name like '%'||#{keyword}||'%'
and acc_address like '%'||#{keyword}||'%'
AND acc_category = ${cate}
ORDER BY acc_rowprice ASC
ORDER BY acc_rowprice DESC
)a)
WHERE rnum >= ${start} AND rnum <= ${end}
