mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT n.not_num, n.not_type,
REPLACE(REPLACE(n.not_title,'<','&lt;'),'>','&gt;') not_title,
n.not_date, n.mem_num, m.mem_id FROM notice n JOIN member m ON n.mem_num=m.mem_num
WHERE not_type = #{not_type}
ORDER BY n.not_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE notice SET 
	
	not_uploadfile=#{not_uploadfile},
	not_filename=#{not_filename},
		
	not_title=#{not_title}, 
	not_content=#{not_content}
WHERE not_num=#{not_num}
