mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT r.rev_num,
rev_title, r.rev_hits, r.rev_date, m.mem_id, m.mem_num FROM review r JOIN member m ON r.mem_num = m.mem_num ORDER BY r.rev_num DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE review SET
rev_uploadfile=#{rev_uploadfile}, rev_filename=#{rev_filename},
rev_title=#{rev_title}, rev_content=#{rev_content}, rev_date=SYSDATE WHERE rev_num=#{rev_num}
